from PIL import Imagefrom dating_app.settings import AVATAR_WATERMARK# from dating_app.settings import AVATAR_WATERMARKclass Watermark:    def process(self, avatar):        watermark = Image.open(AVATAR_WATERMARK)        position = (1100, 1100)        avatar_with_watermark = avatar        avatar_with_watermark.paste(watermark, position, mask=watermark)        return avatar_with_watermark