from rest_framework import serializersfrom rest_framework.fields import HiddenFieldfrom .models import CustomUser, Likeclass UserSerializers(serializers.ModelSerializer):    class Meta:        model = CustomUser        fields = '__all__'class LikeSerializers(serializers.ModelSerializer):    follower = HiddenField(default=serializers.CurrentUserDefault())    def create(self, validated_data):        return Like.objects.create(follower=self.context['follower'], user=self.context['user'])    class Meta:        fields = ['follower']        model = Like